import React,{Component} from 'react'

class Meme extends Component
{
    constructor()
    {
        super()
        this.state={
            uppertext:"",
            lowertext:"",
            randomimg:"",
            alldata:{}
           

        }
        this.handleChange=this.handleChange.bind(this)
    }

    handleChange(event)
    {
        this.setState({
            [event.target.name]:event.target.value
        })
    }
    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes")
        .then(response => response.json())
        .then(response=> {
            const {memes}= response.data
       this.setState({alldata:memes})
        })

    }


    render()
    {
        return (
           <div>
               <div>
               
               <input type="placeholder " name="uppertext"  onChange={this.handleChange} ></input>
               {"            "}
                 {"  "}
               <input type="placeholder " name="lowertext"  onChange={this.handleChange} ></input>
                <button type="submit"> Generate New Meme</button>
                </div>
                <div id="MemeContainer">
                
  <img src={this.state.randomimg} alt="meme" name="randomimg" id="MemeImage"></img>
 
         <h1 id="UpperMemeText"> {this.state.uppertext}</h1>
         <h1 id="LowerMemeText"> {this.state.lowertext}</h1>
         </div>
           </div>
        )
    }
}

export default Meme;
