import React,{Component} from 'react'

class Meme extends Component
{
    constructor()
    {
        super()
        this.state={
            uppertext:""
           

        }
        this.handleChange=this.handleChange.bind(this)
    }

    handleChange(event)
    {
        this.setState({
            uppertext:event.target.value
        })
    }

    render()
    {
        return (
           <div>
               <input type="placeholder " name="uppertext" value="Enter Upper Text" onChange={this.handleChange} ></input>
        <h1> {this.state.uppertext}</h1>
           </div>
        )
    }
}

export default Meme;
