import React,{Component} from 'react'

class Meme extends Component
{
    constructor()
    {
        super()
        this.state={
            uppertext:""
           

        }
        this.handleChange=this.handleChange.bind(this)
    }

    handleChange(event)
    {
        this.setState({
            uppertext:event.target.value
        })
    }

    render()
    {
        return (
           <div>
               <h1><label>Enter Upper Text</label>
               <input type="placeholder " name="uppertext"  onChange={this.handleChange} ></input>
      
  </h1>
  <img src='https://indianmemetemplates.com/storage/are-you-fuing-kidding-me-clean.jpg' alt="meme" id="MemeImage"></img>
         <h1 id="UpperMemeText"> {this.state.uppertext}</h1>
           </div>
        )
    }
}

export default Meme;
